#---------------------------------------#Transformation d'un MTG en Type TreeGraph#---------------------------------------from amlPy import *from openalea.tree_matching import *import syssetmode(1)def mtg2TreeGraph(scale = 1, root = 1):    tree = TreeGraph()    # Root Creation    makeNode(tree,root,0,-1)    return treedef makeNode(tree,Vid,id,father_id):    tree.addNode(id,father_id)    new_father_id = id    for Son_id in Sons(Vid):        id += 1        makeNode(tree,Son_id,id,new_father_id)   